gde_tour:
{
    "name": "create-user",
    "steps": [{
            "step": "toggle menu",
            "title": {
                "ENU": "Open menu"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#menu [data-path='DomainGrant']"
            },
            "next": {
                "event": "toggle"
            }
        },
        {
            "step": "start workflow",
            "title": {
                "ENU": "Stat the user creation process"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#menu [data-path='DomainGrant.wkf.CreateUser']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "set-login",
            "title": {
                "ENU": "Define the user's login"
            },
            "info": {
                "ENU": "Expected value : \"usertest\""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#field_usr_login"
            },
            "next": {
                "condition": (ctn, obj, tour) => {
                    return $("#field_usr_login")?.val() == 'usertest'
                }
            }
        },
        {
            "step": "uesr-create-next",
            "title": {
                "ENU": "Move on"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#wkf_CreateUser button[data-action='validate']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "click line",
            "title": {
                "ENU": "Choose a group"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": ".list-clickable.selected"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "user-group-next",
            "title": {
                "ENU": "Move on"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "button[data-action='validate']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "next-user-resp",
            "title": {
                "ENU": "Move on"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#wkf_CreateUser.activity-NewRespStep button[data-action='validate']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "skip-new-group",
            "title": {
                "ENU": "Move on"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#wkf_CreateUser.activity-AddGroupChoice button[data-action='validate']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "new-user-end",
            "title": {
                "ENU": "You have successfully created a User"
            },
            "info": {
                "ENU": ""
            },
        },
        {
            "step": "activate",
            "title": {
                "ENU": "Activate the user"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "button[data-action='UserStatusActivate']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "confirm-activation",
            "title": {
                "ENU": "Confirm"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#dlgmodal button[data-action='YES']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "reset-password",
            "title": {
                "ENU": "Reset the user's password"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "button[data-action='resetPassword']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "confirm-reset-password",
            "title": {
                "ENU": "Confirm"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#dlgmodal button[data-action='YES']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "copy-password",
            "title": {
                "ENU": "Copy the user's new password"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#dlgmodal > div.modal-dialog.modal-dialog-scrollable > div.modal-content.draggable > div.modal-body > div.alert.alert-warning"
            }
        },
        {
            "step": "ok-reset-password",
            "title": {
                "ENU": "Close the modal"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#dlgmodal button[data-bs-dismiss='modal']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "shotcut-list",
            "title": {
                "ENU": "Open the list of shortcuts"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "left",
                "sel": "#header .btn-shortcut[data-toggle='dropdown']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "clear-cache-shortcut",
            "title": {
                "ENU": "Select \"Clear cache\""
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "left",
                "sel": "#header li[data-shortcut='Cache']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "clear-global-cache",
            "title": {
                "ENU": "Clear all sessions and all server caches"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": ".alert-danger img"
            },
            "next": {
                "event": "click"
            }
        }
    ],
    "exitToast": {
        "ENU": "bye-bye!"
    }
}
