gde_tour:
{
    "name": "create-field",
    "condition": {
        // Init tour when launched
        "init": function(ctn, tour) {
            let def = this;
            // Template exists ?
            let tmpl = $("#field_obo_template_id__tpl_name", ctn);
            if (tmpl.val() != '') {
                return true; //ok
            }
            return "The object first needs a template";
        }
    },
    "steps": [{
            "step": "edit-form",
            "title": {
                "ENU": "Edit the object's form"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "[data-action='editTemplate']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "click-field-area",
            "title": {
                "ENU": "Add an element to the object's form"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#area1 .tmpl-new .btn-insert"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "click-field",
            "title": {
                "ENU": "Add a Field"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": ".new-menu.on [data-menu='field']"
            },
            "next": {
                "condition": (ctn) => {
                    return true;
                }
            }
        },
        {
            "step": "create-field",
            "title": {
                "ENU": "Create a new Field"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "#dlgmodal_field .tmpl-selector #typed"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "select-field",
            "title": {
                "ENU": "Select the Short text type"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": ".newfield [data-field-type='3']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "set-name",
            "title": {
                "ENU": "Set the label for the Field"
            },
            "info": {
                "ENU": "e.g. \"Code\""
            },
            "attach": {
                "pos": "bottom",
                "sel": ".tmpl-form #label"
            },
            "next": {
                "event": "change"
            }
        },
        {
            "step": "required",
            "title": {
                "ENU": "Define the field as Required"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": ".btn-switch:has(#req)"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "save",
            "title": {
                "ENU": "Save the field"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "left",
                "sel": ".modal-footer .btn-SAVE"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "close",
            "title": {
                "ENU": "Close the Template editor"
            },
            "attach": {
                "pos": "lest",
                "sel": "[data-action='close']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "field-end",
            "title": {
                "ENU": "Congratulations !"
            },
            "info": {
                "ENU": "You have successfully added a new field to the object's form"
            }
        }
    ],
    "exitToast": {
        "ENU": "bye-bye!"
    }
}
