gde_tour:
{
    "name": "Create module",
    "steps": [
        // Create module
        {
            "step": "welcome",
            "title": {
                "ENU": "Let's get you started !"
            },
            "info": {
                "ENU": "This guide will help you create your Module"
            }
        },
        {
            "step": "create-module",
            "title": {
                "ENU": "Open the menu"
            },
            "attach": {
                "pos": "right",
                "sel": "#menu [data-path='DomainProject']"
            },
            "next": {
                "event": "toggle"
            }
        },
        {
            "step": "open-module-list",
            "title": {
                "ENU": "Access the list of Modules"
            },
            "attach": {
                "pos": "right",
                "sel": "#menu [data-path='DomainProject.Module']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "start workflow",
            "title": {
                "ENU": "Stat the Module creation process"
            },
            "info": {
                "ENU": ""
            },
            "attach": {
                "pos": "bottom",
                "sel": "[data-action='ModuleCreateProcess']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "module-name",
            "title": {
                "ENU": "Name your module"
            },
            "info": {
                "ENU": "e.g. \"Application\""
            },
            "attach": {
                "pos": "right",
                "sel": "#field_mdl_name"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("#field_mdl_name")?.val() == 'Training'
                }
                */
            }
        }, {
            "step": "module-prefix",
            "title": {
                "ENU": "Set a module prefix",
            },
            "info": {
                "ENU": "e.g. \"app\""
            },
            "attach": {
                "pos": "right",
                "sel": "#field_mdl_prefix"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("#field_mdl_prefix")?.val() == 'trn'
                }
                */
            }
        }, {
            "step": "module-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        }, {
            "step": "group-name",
            "title": {
                "ENU": "Name the Group",
            },
            "info": {
                "ENU": "e.g. : \"APP_SUPERADMIN\""
            },
            "attach": {
                "pos": "right",
                "align": "bottom",
                "sel": "#field_grp_name"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("#field_grp_name")?.val() == 'TRN_SUPERADMIN'
                }
                */
            }
        }, {
            "step": "group-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "domain-name",
            "title": {
                "ENU": "Name the Domain",
            },
            "info": {
                "ENU": "e.g. : \"AppDomain\""
            },
            "attach": {
                "pos": "right",
                "align": "bottom",
                "sel": "#field_obd_name"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("#field_obd_name")?.val() == 'TrnDomain'
                }
                */
            }
        },
        {
            "step": "domain-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "bottom",
                "align": "center",
                "sel": ".activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        }, {
            "step": "domain-tsl",
            "title": {
                "ENU": "Set a label for the Domain",
            },
            "info": {
                "ENU": "e.g. : \"My App Menu\""
            },
            "attach": {
                "pos": "right",
                "align": "bottom",
                "sel": "table input:first"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("table input:first")?.val() == 'My App'
                }
                */
            }
        },
        {
            "step": "tsl-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "domain-grant",
            "title": {
                "ENU": "Grant the Group to the Domain",
            },
            "attach": {
                "pos": "right",
                "sel": ".activity-GrantDomain input[type='checkbox']"
            },
            "next": {
                "event": "click",
            }
        },
        {
            "step": "domain-grant-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-GrantDomain .activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "vue-icon",
            "title": {
                "ENU": "Select an icon for your Application",
            },
            "attach": {
                "pos": "bottom",
                "align": "bottom",
                sel: ".btn-icpkr_field_viw_icon"
            },
            "next": {
                "event": "onload"
            }
        },
        {
            "step": "view-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "module-end",
            "title": {
                "ENU": "Congratulations !"
            },
            "info": {
                "ENU": "You have successfully created your Module"
            }
        }
    ],
    "exitToast": {
        "FRA": "Merci et à bientôt !",
        "ENU": "You have completed the first step of the tutorial ! For more information, please refer to the documentation."
    }
}
