gde_tour:
{
    "name": "Create Object",
    "steps": [
        // Create business object
        {
            "step": "open-businessobject-menu",
            "title": {
                "ENU": "Let's create a Business object !"
            },
            "info": {
                "ENU": "This user guide will take you through the steps to create a Business object"
            }
        },
        {
            "step": "businessobject-process",
            "title": {
                "ENU": "Start the process to create a Business object"
            },
            "attach": {
                "pos": "bottom",
                "align": "center",
                "sel": "[data-action='ObjectCreateProcess']"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "obj-name",
            "title": {
                "ENU": "Set the logical name",
            },
            "info": {
                "ENU": "e.g. \"AppObject\""
            },
            "attach": {
                "pos": "right",
                "sel": "#field_obo_name"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("#field_obo_name")?.val() == 'TrnSupplier'
                }
                */
            }
        }, {
            "step": "obj-name",
            "title": {
                "ENU": "Set the table name",
            },
            "info": {
                "ENU": "e.g. \"app_object\""
            },
            "attach": {
                "pos": "right",
                "sel": "#field_obo_dbtable"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("#field_obo_dbtable")?.val() == 'trn_supplier'
                }
                */
            }
        }, {
            "step": "obj-name",
            "title": {
                "ENU": "Set the object's prefix",
            },
            "info": {
                "ENU": "e.g. \"obj\""
            },
            "attach": {
                "pos": "right",
                "sel": "#field_obo_prefix"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("#field_obo_prefix")?.val() == 'sup'
                }
                */
            }
        },
        {
            "step": "obj-mdl",
            "title": {
                "ENU": "Set the Object's Module",
            },
            "info": {
                "ENU": "e.g. \"Application\""
            },
            "attach": {
                "pos": "right",
                "sel": "#field_row_module_id__mdl_name"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("#field_obo_prefix")?.val() == 'sup'
                }
                */
            }
        },
        {
            "step": "obj-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "obj-tsl",
            "title": {
                "ENU": "Set a label for the Business object",
            },
            "info": {
                "ENU": "e.g. \"Object|Objects\""
            },
            "attach": {
                "pos": "bottom",
                "sel": "table input:first"
            },
            "next": {
                "event": "change",
                /*
                "condition": function(ctn) {
                    return $("table input:first")?.val() == 'Object|Objects'
                }
                */
            }
        },
        {
            "step": "obj-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "obj-grant",
            "title": {
                "ENU": "Grant the Group to the Business object",
            },
            "info": {
                "ENU": "e.g. \"Read, update, create, delete\""
            },
            "attach": {
                "pos": "right",
                "sel": ".worklist select"
            },
            "next": {
                "event": "change",
            }
        },
        {
            "step": "obj-grant-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "domain-next",
            "title": {
                "ENU": "Let's move on to the next step"
            },
            "attach": {
                "pos": "left",
                "sel": ".activity-SelectDomain .activity-footer .btn-validate"
            },
            "next": {
                "event": "click"
            }
        },
        {
            "step": "obj-end",
            "title": {
                "ENU": "Congratulations !"
            },
            "info": {
                "ENU": "You have successfully created your Business object"
            }
        },
    ],
    "exitToast": {
        "FRA": "Merci et à bientôt !",
        "ENU": "You have completed the tutorial ! For more information, please refer to the documentation."
    }
}
